function readingTime(){var e=document.getElementById("main").innerText.trim().split(/\s+/).length,n=Math.ceil(e/175);document.getElementById("readtime").innerText=n}let infographic;function openInfographic(){infographic.classList.toggle("closed")}function setInfographic(){infographic=document.querySelector("section.infographic.popup.closed");infographic.querySelector("header nav > button.close").addEventListener("click",openInfographic)}function bindZoomButtons(){const e=document.getElementsByClassName("zoom infographic");for(var n=0;n<e.length;n++)e[n].addEventListener("click",(function(){openInfographic()}))}jQuery(".page")[0]&&readingTime(),setInfographic(),bindZoomButtons();